import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  const API = "http://localhost:5000/api/tasks";

  // Load tasks
  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await axios.get(API);
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  }

  // Add task
  async function addTask(e) {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      const res = await axios.post(API, { title, description });
      setTasks([...tasks, res.data]); // add new task
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("Error adding task:", err);
    }
  }

  // Mark complete
  async function toggleComplete(task) {
    try {
      const res = await axios.put(`${API}/${task.id}`, {
        ...task,
        completed: !task.completed,
      });
      const updated = res.data;

      // move completed tasks to bottom
      const updatedTasks = tasks.map((t) =>
        t.id === updated.id ? updated : t
      );
      updatedTasks.sort((a, b) => a.completed - b.completed);

      setTasks(updatedTasks);
    } catch (err) {
      console.error("Error updating task:", err);
    }
  }

  // Delete
  async function deleteTask(id) {
    try {
      await axios.delete(`${API}/${id}`);
      setTasks(tasks.filter((t) => t.id !== id));
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  }

  // Start edit
  function startEdit(task) {
    setEditingTask(task);
    setTitle(task.title);
    setDescription(task.description);
  }

  // Save edit
  async function saveEdit(e) {
    e.preventDefault();
    if (!editingTask) return;

    try {
      const res = await axios.put(`${API}/${editingTask.id}`, {
        title,
        description,
        completed: editingTask.completed,
      });
      const updated = res.data;

      setTasks(tasks.map((t) => (t.id === updated.id ? updated : t)));
      setEditingTask(null);
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("Error editing task:", err);
    }
  }

  return (
    <div className="app">
      <header>
        <h1>Task Manager</h1>
      </header>

      <main>
        <form onSubmit={editingTask ? saveEdit : addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <button type="submit">
            {editingTask ? "Save Changes" : "Add Task"}
          </button>
        </form>

        <section className="tasks">
          {tasks.length === 0 && (
            <p className="empty">No tasks yet â€” add one!</p>
          )}
          {tasks.map((task) => (
            <article
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
            >
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div className="task-actions">
                <button onClick={() => toggleComplete(task)}>
                  {task.completed ? "Undo" : "Complete"}
                </button>
                <button onClick={() => startEdit(task)}>Edit</button>
                <button
                  onClick={() => deleteTask(task.id)}
                  className="danger"
                >
                  Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
