i// client/src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  // ‚úÖ Fetch tasks from backend
  useEffect(() => {
    axios.get("http://localhost:5000/api/tasks")
      .then(res => setTasks(res.data))
      .catch(err => console.error("‚ùå Error fetching tasks:", err));
  }, []);

  // ‚úÖ Add new task
  const addTask = () => {
    if (!newTask.trim()) return;

    axios.post("http://localhost:5000/api/tasks", { title: newTask })
      .then(res => {
        setTasks([...tasks, res.data]); // show immediately
        setNewTask(""); // clear input
      })
      .catch(err => console.error("‚ùå Error adding task:", err));
  };

  return (
    <div className="App">
      <h1>üìù Task Manager</h1>

      <div className="task-input">
        <input
          type="text"
          placeholder="Enter a new task..."
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <div className="task-list">
        {tasks.length === 0 ? (
          <p>No tasks yet...</p>
        ) : (
          tasks.map(task => (
            <div key={task.id} className="task-box">
              {task.title}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
