import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const API = "/api/tasks";

  // ✅ Fetch tasks from backend
  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await axios.get(API);
      setTasks(res.data);
    } catch (err) {
      console.error("❌ Fetch error:", err.message);
    }
  }

  // ✅ Add a new task
  async function addTask(e) {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      const res = await axios.post(API, { title, description });
      setTasks([res.data, ...tasks]); // add new task on top
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("❌ Add error:", err.message);
    }
  }

  // ✅ Toggle complete and push completed tasks to bottom
  async function completeTask(task) {
    try {
      const res = await axios.put(`${API}/${task.id}`, {
        ...task,
        completed: 1,
      });

      const updated = res.data;

      setTasks((prev) => {
        const newTasks = prev.map((t) => (t.id === updated.id ? updated : t));
        // Reorder: incomplete tasks first, completed last
        return [
          ...newTasks.filter((t) => !t.completed),
          ...newTasks.filter((t) => t.completed),
        ];
      });
    } catch (err) {
      console.error("❌ Complete error:", err.message);
    }
  }

  // ✅ Delete a task
  async function deleteTask(id) {
    try {
      await axios.delete(`${API}/${id}`);
      setTasks(tasks.filter((t) => t.id !== id));
    } catch (err) {
      console.error("❌ Delete error:", err.message);
    }
  }

  return (
    <div className="app">
      <header>
        <h1> Task Manager</h1>
        <p>Runs with MySQL Workbench</p>
      </header>

      <main>
        <form onSubmit={addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
            required
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <button type="submit">Add Task</button>
        </form>

        <section className="tasks">
          {tasks.length === 0 && <p className="empty">No tasks yet — add one!</p>}
          {tasks.map((task) => (
            <article
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
            >
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div className="task-actions">
                {!task.completed && (
                  <button onClick={() => completeTask(task)}>✅ Complete</button>
                )}
                <button
                  onClick={() => deleteTask(task.id)}
                  className="danger"
                >
                  Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>

      <footer>
        <small>Client:3000 → Server:5000 → MySQL Workbench</small>
      </footer>
    </div>
  );
}

export default App;
