import React, { useEffect, useState } from 'react';
import './styles.css';

function App(){
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [editing, setEditing] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const API = '/api/tasks';

  useEffect(()=>{ fetchTasks(); }, []);

  async function fetchTasks(){
    try{
      const res = await fetch(API);
      const data = await res.json();
      setTasks(data.map(t=>({...t, completed: t.completed===1 || t.completed===true})));
    }catch(e){
      console.error('fetchTasks', e);
      setTasks([]);
    }
  }

  async function addTask(e){
    e.preventDefault();
    if(!title.trim()) return;
    const res = await fetch(API, {
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify({ title: title.trim(), description: description.trim() })
    });
    const newTask = await res.json();
    setTasks(prev => [{...newTask, completed: newTask.completed===1}, ...prev]);
    setTitle(''); setDescription('');
  }

  function startEdit(task){
    setEditing(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description || '');
  }
  function cancelEdit(){ setEditing(null); setEditTitle(''); setEditDescription(''); }

  async function saveEdit(id){
    try{
      const res = await fetch(`${API}/${id}`, {
        method:'PUT',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({ title: editTitle, description: editDescription, completed: tasks.find(t=>t.id===id).completed })
      });
      const updated = await res.json();
      setTasks(prev => prev.map(t=> t.id===updated.id ? {...updated, completed: updated.completed===1} : t));
      cancelEdit();
    }catch(e){ console.error('saveEdit', e); }
  }

  async function toggleComplete(task){
    try{
      const res = await fetch(`${API}/${task.id}`, {
        method:'PUT',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({ title: task.title, description: task.description, completed: !task.completed })
      });
      const updated = await res.json();
      setTasks(prev => prev.map(t=> t.id===updated.id ? {...updated, completed: updated.completed===1} : t));
    }catch(e){ console.error('toggleComplete', e); }
  }

  async function deleteTask(id){
    try{
      await fetch(`${API}/${id}`, { method:'DELETE' });
      setTasks(prev => prev.filter(t=>t.id!==id));
    }catch(e){ console.error('deleteTask', e); }
  }

  return (
    <div className="app">
      <header><h1>ðŸŒˆ Task Manager</h1><p>Runs with XAMPP MySQL</p></header>
      <main>
        <form onSubmit={addTask} className="task-form">
          <input value={title} onChange={e=>setTitle(e.target.value)} placeholder="Task title" />
          <input value={description} onChange={e=>setDescription(e.target.value)} placeholder="Description (optional)" />
          <button type="submit">Add Task</button>
        </form>

        <section className="tasks">
          {tasks.length===0 && <p className="empty">No tasks yet â€” add one!</p>}
          {tasks.map(task => (
            <article key={task.id} className={`task ${task.completed ? 'done' : ''}`}>
              <div className="task-main">
                {editing===task.id ? (
                  <>
                    <input className="edit-title" value={editTitle} onChange={e=>setEditTitle(e.target.value)} />
                    <textarea className="edit-desc" value={editDescription} onChange={e=>setEditDescription(e.target.value)} />
                  </>
                ) : (
                  <>
                    <h3>{task.title}</h3>
                    <p>{task.description}</p>
                  </>
                )}
              </div>

              <div className="task-actions">
                {editing===task.id ? (
                  <>
                    <button onClick={()=>saveEdit(task.id)}>Save</button>
                    <button onClick={cancelEdit} className="danger">Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={()=>toggleComplete(task)}>{task.completed ? 'Undo' : 'Done'}</button>
                    <button onClick={()=>startEdit(task)}>Edit</button>
                    <button onClick={()=>deleteTask(task.id)} className="danger">Delete</button>
                  </>
                )}
              </div>
            </article>
          ))}
        </section>
      </main>
      <footer><small>Client 3000 â†’ proxy â†’ Server 5000</small></footer>
    </div>
  );
}

export default App;
