import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("/api/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  const addTask = async (e) => {
    e.preventDefault();
    if (!title) return;

    try {
      if (editingTask) {
        const res = await axios.put(`/api/tasks/${editingTask.id}`, {
          title,
          description,
          completed: editingTask.completed,
        });
        setTasks(tasks.map((t) => (t.id === editingTask.id ? res.data : t)));
        setEditingTask(null);
      } else {
        const res = await axios.post("/api/tasks", { title, description });
        setTasks([res.data, ...tasks]);
      }
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("Error adding/updating task:", err);
    }
  };

  const deleteTask = async (id) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to delete this task?"
    );
    if (!confirmDelete) return;

    try {
      await axios.delete(`/api/tasks/${id}`);
      setTasks(tasks.filter((t) => t.id !== id));
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  const startEdit = (task) => {
    setTitle(task.title);
    setDescription(task.description);
    setEditingTask(task);
  };

  return (
    <div className="app">
      <header>
        <h1>Task Manager</h1>
        <p>With MySQL persistence</p>
      </header>

      <main>
        <form onSubmit={addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <button type="submit">
            {editingTask ? "Update Task" : "Add Task"}
          </button>
        </form>

        <section className="tasks">
          {tasks.length === 0 && (
            <p className="empty">No tasks yet — add one!</p>
          )}
          {tasks.map((task) => (
            <article key={task.id} className="task">
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div className="task-actions">
                <button onClick={() => startEdit(task)}>✏️ Edit</button>
                <button onClick={() => deleteTask(task.id)} className="danger">
                  ❌ Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
