// client/src/App.js
import React, { useEffect, useState } from "react";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const API = "/api/tasks";

  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await fetch(API);
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      console.error("‚ùå Error fetching tasks:", err);
    }
  }

  async function addTask(e) {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      const res = await fetch(API, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ title, description }),
      });

      if (!res.ok) throw new Error("Failed to add task");

      // ‚úÖ Refresh task list from DB
      await fetchTasks();

      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("‚ùå Error adding task:", err);
    }
  }

  async function toggleComplete(task) {
    try {
      const res = await fetch(`${API}/${task.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: task.title,
          description: task.description,
          completed: !task.completed,
        }),
      });

      if (!res.ok) throw new Error("Failed to update task");
      await fetchTasks();
    } catch (err) {
      console.error("‚ùå Error toggling task:", err);
    }
  }

  async function deleteTask(id) {
    try {
      await fetch(`${API}/${id}`, { method: "DELETE" });
      await fetchTasks();
    } catch (err) {
      console.error("‚ùå Error deleting task:", err);
    }
  }

  return (
    <div className="app">
      <header>
        <h1>üåà Task Manager</h1>
        <p>Runs with XAMPP MySQL</p>
      </header>

      <main>
        <form onSubmit={addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <button type="submit">Add Task</button>
        </form>

        <section className="tasks">
          {tasks.length === 0 && <p>No tasks yet ‚Äî add one!</p>}
          {tasks.map((task) => (
            <article
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
            >
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div className="task-actions">
                <button onClick={() => toggleComplete(task)}>
                  {task.completed ? "Undo" : "Done"}
                </button>
                <button onClick={() => deleteTask(task.id)} className="danger">
                  Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>

      <footer>
        <small>Client 3000 ‚Üí proxy ‚Üí Server 5000</small>
      </footer>
    </div>
  );
}

export default App;
