import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  const API = "/api/tasks";

  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await axios.get(API);
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  }

  async function addTask(e) {
    e.preventDefault();
    if (!title.trim()) return;
    await axios.post(API, { title, description });
    await fetchTasks();
    setTitle("");
    setDescription("");
  }

  function startEdit(task) {
    setEditingTask(task);
    setTitle(task.title);
    setDescription(task.description || "");
  }

  async function saveEdit(e) {
    e.preventDefault();
    if (!editingTask) return;
    await axios.put(`${API}/${editingTask.id}`, {
      title,
      description,
      completed: editingTask.completed,
    });
    await fetchTasks();
    setEditingTask(null);
    setTitle("");
    setDescription("");
  }

  async function deleteTask(id) {
    await axios.delete(`${API}/${id}`);
    await fetchTasks();
  }

  async function toggleComplete(task) {
    await axios.put(`${API}/${task.id}`, {
      title: task.title,
      description: task.description,
      completed: task.completed ? 0 : 1,
    });
    await fetchTasks();
  }

  return (
    <div className="app">
      <header>
        <h1>Task Manager</h1>
      </header>

      <main>
        <form onSubmit={editingTask ? saveEdit : addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
          />
          <button type="submit">
            {editingTask ? "Save Edit" : "Add Task"}
          </button>
          {editingTask && (
            <button
              type="button"
              onClick={() => {
                setEditingTask(null);
                setTitle("");
                setDescription("");
              }}
            >
              Cancel
            </button>
          )}
        </form>

        <section className="tasks">
          {tasks.map((task) => (
            <article key={task.id} className={task.completed ? "done" : ""}>
              <h3>{task.title}</h3>
              <p>{task.description}</p>
              <div className="task-actions">
                <button onClick={() => toggleComplete(task)}>
                  {task.completed ? "Undo" : "Complete"}
                </button>
                <button onClick={() => startEdit(task)}>Edit</button>
                <button onClick={() => deleteTask(task.id)}>Delete</button>
              </div>
            </article>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
