// client/src/App.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [editingTask, setEditingTask] = useState(null);

  // âœ… Base API
  const API = "/api/tasks";

  // âœ… Fetch all tasks on load
  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await axios.get(API);
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  }

  // âœ… Add new task
  async function addTask(e) {
    e.preventDefault();
    if (!title.trim()) return;
    try {
      await axios.post(API, { title, description });
      await fetchTasks();   // ðŸ”‘ reload DB
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("Error adding task:", err);
    }
  }

  // âœ… Start editing
  function startEdit(task) {
    setEditingTask(task);
    setTitle(task.title);
    setDescription(task.description || "");
  }

  // âœ… Save edit
  async function saveEdit(e) {
    e.preventDefault();
    if (!editingTask) return;

    try {
      await axios.put(`${API}/${editingTask.id}`, {
        title,
        description,
        completed: editingTask.completed,
      });
      await fetchTasks();   // ðŸ”‘ reload DB
      setEditingTask(null);
      setTitle("");
      setDescription("");
    } catch (err) {
      console.error("Error editing task:", err);
    }
  }

  // âœ… Delete task
  async function deleteTask(id) {
    try {
      await axios.delete(`${API}/${id}`);
      await fetchTasks();   // ðŸ”‘ reload DB
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  }

  // âœ… Complete task
  async function toggleComplete(task) {
    try {
      await axios.put(`${API}/${task.id}`, {
        title: task.title,
        description: task.description,
        completed: task.completed ? 0 : 1,
      });
      await fetchTasks();   // ðŸ”‘ reload DB
    } catch (err) {
      console.error("Error toggling complete:", err);
    }
  }

  return (
    <div className="app">
      <header>
        <h1>Task Manager</h1>
        <p>Connected with MySQL</p>
      </header>

      <main>
        <form onSubmit={editingTask ? saveEdit : addTask} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
          />
          <button type="submit">
            {editingTask ? "Save Edit" : "Add Task"}
          </button>
          {editingTask && (
            <button
              type="button"
              onClick={() => {
                setEditingTask(null);
                setTitle("");
                setDescription("");
              }}
            >
              Cancel
            </button>
          )}
        </form>

        <section className="tasks">
          {tasks.length === 0 && (
            <p className="empty">No tasks yet â€” add one!</p>
          )}

          {tasks.map((task) => (
            <article
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
            >
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div className="task-actions">
                <button onClick={() => toggleComplete(task)}>
                  {task.completed ? "Undo" : "Complete"}
                </button>
                <button onClick={() => startEdit(task)}>Edit</button>
                <button onClick={() => deleteTask(task.id)} className="danger">
                  Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>

      <footer>
        <small>Client: 3000 â†’ API: 5000</small>
      </footer>
    </div>
  );
}

export default App;
