// client/src/App.js
import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [editingId, setEditingId] = useState(null);

  // Use relative path so proxy (create-react-app) forwards to backend if configured.
  const API_BASE = "/api/tasks";

  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const res = await fetch(API_BASE);
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      console.error("Fetch tasks error:", err);
    }
  }

  // Add or Save (edit)
  async function handleSubmit(e) {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      if (editingId === null) {
        // Create
        const res = await fetch(API_BASE, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ title: title.trim(), description }),
        });
        if (!res.ok) throw new Error("Create failed");
        const created = await res.json();
        // Put newest on top
        setTasks((prev) => [created, ...prev]);
        setTitle("");
        setDescription("");
      } else {
        // Save edit
        const res = await fetch(`${API_BASE}/${editingId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ title: title.trim(), description }),
        });
        if (!res.ok) throw new Error("Update failed");
        const updated = await res.json();
        setTasks((prev) =>
          prev.map((t) => (t.id === updated.id ? updated : t))
        );
        setEditingId(null);
        setTitle("");
        setDescription("");
      }
    } catch (err) {
      console.error("Submit error:", err);
    }
  }

  // Delete
  async function handleDelete(id) {
    try {
      const res = await fetch(`${API_BASE}/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Delete failed");
      setTasks((prev) => prev.filter((t) => t.id !== id));
    } catch (err) {
      console.error("Delete error:", err);
    }
  }

  // Toggle complete (works by sending only completed; backend merges fields)
  async function handleToggleComplete(task) {
    try {
      const res = await fetch(`${API_BASE}/${task.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ completed: task.completed ? 0 : 1 }),
      });
      if (!res.ok) throw new Error("Toggle complete failed");
      const updated = await res.json();
      // Refresh UI ordering: incomplete first (0), then completed (1)
      setTasks((prev) => {
        const newTasks = prev.map((t) => (t.id === updated.id ? updated : t));
        newTasks.sort(
          (a, b) =>
            a.completed - b.completed ||
            new Date(b.created_at) - new Date(a.created_at)
        );
        return [...newTasks];
      });
    } catch (err) {
      console.error("Complete error:", err);
    }
  }

  // Start edit: populate form with the task
  function startEdit(task) {
    setEditingId(task.id);
    setTitle(task.title || "");
    setDescription(task.description || "");
    // focus input optionally
    const el = document.querySelector(".task-form input");
    if (el) el.focus();
  }

  // Cancel edit
  function cancelEdit() {
    setEditingId(null);
    setTitle("");
    setDescription("");
  }

  return (
    <div className="app">
      <header>
        <h1>Task Manager</h1>
      </header>

      <main>
        <form onSubmit={handleSubmit} className="task-form">
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Task title"
            aria-label="Task title"
          />
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description (optional)"
            aria-label="Task description"
          />
          <button type="submit">
            {editingId ? "Save Changes" : "Add Task"}
          </button>
          {editingId && (
            <button
              type="button"
              onClick={cancelEdit}
              style={{ marginLeft: 8 }}
            >
              Cancel
            </button>
          )}
        </form>

        <section className="tasks">
          {tasks.length === 0 && (
            <p className="empty">No tasks yet â€” add one!</p>
          )}

          {tasks.map((task) => (
            <article
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
            >
              <div className="task-main">
                <h3>{task.title}</h3>
                <p>{task.description}</p>
              </div>

              <div className="task-actions">
                {/* all action buttons MUST be type="button" to avoid submitting the form */}
                <button
                  type="button"
                  onClick={() => handleToggleComplete(task)}
                >
                  {task.completed ? "Undo" : "Complete"}
                </button>

                <button type="button" onClick={() => startEdit(task)}>
                  Edit
                </button>

                <button
                  type="button"
                  className="danger"
                  onClick={() => handleDelete(task.id)}
                >
                  Delete
                </button>
              </div>
            </article>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
